function maxNumber(num1, num2, num3) {
    const maxNum = Math.max(num1, num2, num3)
    return maxNum;
}
const maxNumIs = maxNumber(18, 46, 69)
console.log('Max number:', maxNumIs)

function minNumber (num1, num2, num3){
    const minNumber = Math.min(num1, num2, num3)
    return minNumber
}
const minNumberIs = minNumber(-983300, -87490, 8459)
console.log('Min number:', minNumberIs)